INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('ef27a5b0-8712-4117-abe7-6c6aa16212b1', 'b7de9416-42a4-479e-a300-39898a0dd6be', '7', 'Transfer to the bowl with the asparagus.To make the dressing, combine the tarragon, lemon zest, garlic, lemon juice, olive oil and mustard in a small food processor or blender. Process until smooth.');
