INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('0861af04-b334-40ac-8bc6-3ad3b1216356', '3a035b3c-aead-4448-9c61-e58e73b81c1e', '2', 'Drain and rinse the kidney beans, then transfer to a medium saucepan and cover with fresh water. Bring to a boil, reduce heat to medium-low, cover, and simmer for 1 hour or until just tender but not falling apart.');
