INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('7b71118c-9c85-44a8-85b7-838fb9d62e8c', '3a035b3c-aead-4448-9c61-e58e73b81c1e', '4', 'Heat the oil in a large saucepan over medium heat. When hot, add the onion and saut for 5 minutes. Now add the garlic, carrots, celery and green beans, and stir for another 5 minutes. Next add the tomatoes, red pepper, eggplant, sage, thyme, marjoram and celery seed, and continue to stir for another few minutes.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('ca3d8bd0-db0c-4661-a30a-9dcacb321b76', '3a035b3c-aead-4448-9c61-e58e73b81c1e', '5', 'Pour in the vegetable stock, liquid smoke, rice and the cooked kidney beans. Bring to a boil, reduce heat to medium low, cover, and cook, stirring occasionally, for 45 minutes or until the rice is tender.');
