INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('076d81c1-7294-4049-b69c-3e463ace992d', '36c50dde-dbab-44cf-b26b-309b66d22916', '2', 'Drain and rinse, then transfer to a large saucepan and cover with fresh water. Bring to a boil, reduce heat to medium-low, cover, and simmer for 40 to 60 minutes. Take care not to overcook  the beans should be tender but not be falling apart.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('c47d7291-e4b2-4093-8997-1708cfe54374', '36c50dde-dbab-44cf-b26b-309b66d22916', '3', 'Drain and set aside.To prepare the eggplant, cut of the stem and bottom edge and then cut in half lengthwise. Score the flesh into diagonal 1-inch lines, then turn and score again until you have a diagonal pattern. Take care not to cut through the skin.');
