INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('386edce9-1281-420f-9f8b-2b8161438b91', '36c50dde-dbab-44cf-b26b-309b66d22916', '5', 'Brush the eggplant with some oil and transfer to a roasting pan.');
