INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('fe9e4bdf-ba01-4113-816c-7223d08973db', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '1', 'Spread the dry beans out on a baking sheet and pick out any pebbles.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('a72748e1-1dd2-42f4-8d42-36eb945475b9', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '2', 'Place the beans in a big soup pot, cover with water by 3 inches and allow them to soak overnight or for 6-8 hours, then discard that water. Return the soaked beans to the pot and cover with 3 inches of water again, bring to a boil. Reduce heat to medium low, put the cover on and allow to cook until the beans start to get tender but still firm, about 1 1/2 hours.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('3c2e226d-1446-4620-9285-14d9cff5e16d', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '3', 'Drain the beans.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('66c96496-e284-4fcb-a2f4-80bb45b87048', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '4', 'Heat the oil in a soup pot over med-high heat.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('a2f02bf8-a5ff-4b44-81aa-c0bea8f11af3', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '5', 'Add the onion along with a pinch of salt and saute until softened and golden. Stir in 1 tbsp of ground cumin, add the beans along with the broth or water and bring to a boil. Reduce heat to medium-low, cover and cook for 30 min. Meanwhile peel and chop the sweet potatoes. Wash the kale, remove the stems and chop the leaves.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('a709b567-3bab-41d9-b796-213f059e0342', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '6', 'Remove half of the beans and liquid and set aside to cool a bit.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('43180dcd-4d4b-4ace-a22b-35d120b7520a', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '7', 'Add the sweet potatoes and kale to the pot with some salt and pepper. Cover and cook for 10 minutes.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('7cb5ae06-03b6-48a6-b159-e2270a9ba9aa', 'b9a39094-21d7-43ab-93b4-abe0d83e25e2', '8', 'Transfer the cooled beans to a blender and puree until smooth, then return them to the pot. Stir in the remaining 1 tbsp of ground cumin. Now add 1 tbsp of the chipotles in adobo. Taste and continue to add more until it has a spice level that you like. Adjust the salt &amp; pepper as needed.');
