INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('94671a5d-e659-4b27-958d-b8abb8b69958', '9fb29be6-3190-4ebd-a6ef-c7d2d113dc86', '7', 'Place the steak on a sheet of plastic wrap. Smear the spinach mixture evenly over the steak, leaving about 1 inch border along the side edge. Beginning with the side near you and using the plastic wrap as an aid, rollup the steak, gently pressing on the filling.');
