INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('8f296277-df7f-4d27-8cf6-d13ebe8dc929', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '2', 'Spray a cookie sheet with nonstick spray.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('0024f307-a50c-4ba5-8227-d9c8d9e5acac', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '3', 'Toss butternut squash with about 1 tbsp olive oil (possibly less, just enough to coat) and season with salt and pepper.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('ced85eec-1418-43e2-8286-102b06e2afdd', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '4', 'Spread in one layer on cookie sheet.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('650f0744-8fc0-481a-b7c2-21a2aa8029c0', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '5', 'Roast butternut squash for 20-25 minutes, until fork tender.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('d83b40d4-8b97-4b32-a5c2-d15899af043c', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '6', 'While squash is roasting, combine quinoa and water or stock in a saucepan. If using water, season with 1/2 tsp salt. Bring to a boil, lower heat, cover and simmer for 10-15 minutes, until liquid is absorbed.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('2c78c006-383b-4551-9a20-01ed8abb4053', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '7', 'In a large bowl, combine cooked quinoa, butternut squash, arugula, 1 tbsp olive oil, balsamic vinegar and crumbled goat cheese. Season with a pinch more salt and pepper. Toss well to mix everything and wilt arugula.');
INSERT INTO "cooking_steps"("guid","recipe_id","order_number","description") VALUES('42717a13-e09b-4342-8c44-664652069b9a', 'c0dead68-809f-4614-a65d-b66e92d7d6af', '8', 'Serve immediately, garnished with toasted nuts, if desired.');
